import { Link } from "react-router-dom"
import { useParams } from "react-router-dom";
import contactArr from "../data"
// import contacArr from "../data"

import HomePage from "./HomePage"













// const {photo} = contacArr
// console.log(photo)

export default function EmploeePage({contact}){
    const {id} = useParams()

    // console.log(contactArr[0]);

    const thiscontact = contactArr[id]

     console.log(thiscontact.name)


    return (
        <div className="personalInfo">
            <Link to="/" element = {<HomePage/>}>
                <div>
                    <h2><i className="arrow left"></i> Back  </h2>
                </div>
            </Link>

            <div className="personalTitle">

                <img src = {thiscontact.img} alt= 'Profile Picture' style={{ height: "150px", borderRadius: "100px" }} />
                <div className="personalName">
                    <p className="name">{thiscontact.name}</p>
                    <br/>
                    <p className="position">{thiscontact.position}</p>
                </div>
            </div>

            <div>
                Call Ofiice
                {thiscontact.office}
            </div>

            <div>
                Call Mobile
                {thiscontact.mobile}
            </div>

            <div>
                SMS
                {thiscontact.mobile}
            </div>

            <div>
                Email
                {thiscontact.email}
            </div>


        </div>

    )
}
