import { Link } from "react-router-dom"
import { useParams } from "react-router-dom";
import contactArr from "../data"
// import contacArr from "../data"

import HomePage from "./HomePage"













// const {photo} = contacArr
// console.log(photo)

export default function EmploeePage({contact}){
    const {id} = useParams()

    // console.log(contactArr[0]);

    const thiscontact = contactArr[id]

     console.log(thiscontact.name)


    return (
        <>
            <Link to="/" element = {<HomePage/>}>
                <div><h1>Emploee Page here {thiscontact.name} </h1></div>
            </Link>

            <div>
                <img src = {thiscontact.img} alt= 'Profile Picture' style={{ height: "150px", borderRadius: "100px" }} />
            </div>

            <div>
                {thiscontact.name}
                {thiscontact.position}
            </div>
            <div>
                Call Ofiice
                {thiscontact.office}
            </div>
            <div>
                Call Mobile
                {thiscontact.mobile}
            </div>
            <div>
                SMS
                {thiscontact.mobile}
            </div>


        </>

    )
}
